import React, { useState, useContext } from "react";
import { signup as apiSignup, login as apiLogin } from "../../api";
import { UserContext } from "../../UserContext";

export default function Signup({onSwitch}) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const { login } = useContext(UserContext);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    try {
      await apiSignup(username, password);
      const res = await apiLogin(username, password);
      login(res.access_token, res.username);
    } catch (err) {
      setError("Signup failed (maybe username exists)");
    }
  };

  return (
    <div>
      <h2>Signup</h2>
      <form onSubmit={handleSubmit}>
        <input placeholder="Username" value={username} onChange={e=>setUsername(e.target.value)} /><br/>
        <input type="password" placeholder="Password" value={password} onChange={e=>setPassword(e.target.value)} /><br/>
        <button type="submit">Signup</button>
      </form>
      {error && <div style={{color:"red"}}>{error}</div>}
      <div>
        <button onClick={onSwitch}>Have an account? Login</button>
      </div>
    </div>
  );
}
