import React, { useState } from "react";
import { getSentiment } from "../api";

export default function Sentiment({ selectedIPO }) {
    const [sentiment, setSentiment] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    const handleFetch = async () => {
        setError("");
        setSentiment(null);
        setLoading(true);
        try {
            const res = await getSentiment(selectedIPO.IPO_Name);
            setSentiment(res);
        } catch (e) {
            setError("Sentiment analysis failed.");
        }
        setLoading(false);
    };

    if (!selectedIPO) return null;

    return (
        <div style={{ marginBottom: 16 }}>
            <h3>NLP: Social Media Sentiment</h3>
            <button onClick={handleFetch} disabled={loading}>
                {loading ? "Fetching..." : "Fetch Sentiment"}
            </button>
            {error && <div style={{ color: "red", marginTop: 8 }}>{error}</div>}
            {sentiment && (
                <div style={{ marginTop: 8 }}>
                    <b>Avg Sentiment (VADER):</b> {sentiment.avg_sentiment.toFixed(2)}
                    <div style={{marginTop:8}}>
                        <b>Sample Tweets:</b>
                        <ul>{sentiment.sample_tweets.map((t,i) => <li key={i}>{t}</li>)}</ul>
                    </div>
                </div>
            )}
        </div>
    );
}
