import React, { useEffect, useState } from "react";
import { fetchIPOs } from "../api";

export default function IPOSelector({ onSelectIPO }) {
    const [ipos, setIPOs] = useState([]);
    const [selected, setSelected] = useState("");

    useEffect(() => {
        fetchIPOs().then(setIPOs);
    }, []);

    const handleChange = (e) => {
        setSelected(e.target.value);
        const ipo = ipos.find(ipo => ipo.IPO_Name === e.target.value);
        onSelectIPO(ipo || null);
    };

    return (
        <div style={{ marginBottom: 16 }}>
            <label>
                <b>Select IPO:</b>
                <select value={selected} onChange={handleChange} style={{ marginLeft: 8 }}>
                    <option value="">--Choose an IPO--</option>
                    {ipos.map((ipo) => (
                        <option key={ipo.IPO_Name} value={ipo.IPO_Name}>
                            {ipo.IPO_Name}
                        </option>
                    ))}
                </select>
            </label>
        </div>
    );
}
