import React, { useEffect, useState } from "react";
import { fetchIPOs } from "../api";

export default function IPOSelector({ onSelectIPO }) {
    const [ipos, setIPOs] = useState([]);
    const [selected, setSelected] = useState("");

    useEffect(() => {
        fetchIPOs()
            .then(data => {
                if (Array.isArray(data)) {
                    setIPOs(data);
                } else {
                    setIPOs([]); // fallback to empty array if not array
                    console.error("fetchIPOs did not return an array:", data);
                }
            })
            .catch(err => {
                setIPOs([]);
                console.error("Error fetching IPOs:", err);
            });
    }, []);

    const handleChange = (e) => {
        setSelected(e.target.value);
        const ipo = ipos.find(ipo => ipo.IPO_Name === e.target.value);
        onSelectIPO(ipo || null);
    };

    return (
    <div style={{ marginBottom: 16 }}>
        <label>
            <b>Select IPO:</b>
            <select value={selected} onChange={handleChange} style={{ marginLeft: 8 }}>
                <option value="">--Choose an IPO--</option>
                {Array.isArray(ipos) && ipos.length > 0 ? (
                    ipos.map((ipo) => (
                        <option key={ipo.IPO_Name} value={ipo.IPO_Name}>
                            {ipo.IPO_Name}
                        </option>
                    ))
                ) : (
                    <option disabled>No IPOs available</option>
                )}
            </select>
        </label>
    </div>
);
}
