import React from "react";

export default function IPOInfo({ ipo }) {
    if (!ipo) return null;
    return (
        <div style={{ marginBottom: 16, border: "1px solid #ccc", padding: 12, borderRadius: 4 }}>
            <h2>{ipo.IPO_Name}</h2>
            <div><b>Listing Date:</b> {ipo.Date}</div>
            <div><b>Issue Size:</b> ₹{ipo['Issue_Size(crores)']} Cr</div>
            <div><b>QIB:</b> {ipo.QIB} | <b>HNI:</b> {ipo.HNI} | <b>RII:</b> {ipo.RII}</div>
            <div><b>Issue Price:</b> ₹{ipo.Issue_price}</div>
            <div><b>Listing Open:</b> ₹{ipo.Listing_Open} | <b>Close:</b> ₹{ipo.Listing_Close}</div>
            <div><b>Listing Gains(%):</b> {ipo['Listing_Gains(%)']}%</div>
            <div><b>Current Market Price:</b> ₹{ipo.CMP} | <b>Current Gains:</b> {ipo.Current_gains}</div>
        </div>
    );
}
