import axios from "axios";
const API_URL = "http://localhost:5000/api";

export function setAuthToken(token) {
  if (token)
    axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
  else
    delete axios.defaults.headers.common["Authorization"];
}

export async function signup(username, password) {
  return (await axios.post(`${API_URL}/signup`, {username, password})).data;
}
export async function login(username, password) {
  return (await axios.post(`${API_URL}/login`, {username, password})).data;
}
export async function fetchMe() {
  return (await axios.get(`${API_URL}/me`)).data;
}

export async function fetchIPOs() {
  // Use a fresh axios instance with NO auth header
  return (await axios.get(`${API_URL}/ipos`, { headers: { Authorization: undefined } })).data;
}

export async function fetchIPOByName(name) {
  return (await axios.get(`${API_URL}/ipo/${encodeURIComponent(name)}`)).data;
}
export async function calcProfitLoss(ipo_name, qty, sell_price) {
  return (await axios.post(`${API_URL}/profitloss`, { ipo_name, qty, sell_price })).data;
}
export async function mlPredict(features) {
  return (await axios.post(`${API_URL}/ml_predict`, features)).data;
}
export async function fetchIPOTrends() {
  return (await axios.get(`${API_URL}/ipo_trends`)).data;
}
export async function getSentiment(ipo_name) {
  return (await axios.post(`${API_URL}/ipo_sentiment`, { ipo_name })).data;
}
